import React, { Component } from 'react';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';

const mapCenter = [3.43722 , -76.5225];
const zoomLevel = 12.4;

const iconImage = new L.Icon({
    iconUrl: require('./images/marker.svg'),
    iconSize: new L.point(25,25),
    className: 'leaflet-div-icon'
}) 
class LMap extends Component {
    constructor(props) {
        super(props);
        this.state = {
            size :{
                height : this.props.height,
                width : this.props.width,
            },
            punto : {
                lat: 1.0,
                lng: 1.0,
            },
            markers: this.props.markers
        }
    }
    doSomething = (e) => {
        this.setState({ punto: {lat:e.latlng.lat, lng:e.latlng.lng}});
        this.props.point(this.state.punto)
    }
    render() {
        return (
            <div>
                <center>
                <Map
                    style = {{height: this.state.size.height, width: this.state.size.width}}
                    center={mapCenter}
                    zoom={zoomLevel}
                    zoomControl={false}
                    doubleClickZoom={false}
                    onClick={this.doSomething}
                >
                    <TileLayer
                        url = 'https://api.tiles.mapbox.com/v4/{id}/{z}/{x}/{y}.png?access_token=pk.eyJ1IjoidmFsZXJpYXJtIiwiYSI6ImNqdDU3Z285aTAzaWMzeW8zcWw2dzJ5c2gifQ.44erZi0QyJwNw__0LsjcQQ'
                        attribution= 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, Imagery Â© <a href="https://www.mapbox.com/">Mapbox</a>'
                        id= 'mapbox.streets'
                        accessTocken= 'pk.eyJ1IjoidmFsZXJpYXJtIiwiYSI6ImNqdDU3Z285aTAzaWMzeW8zcWw2dzJ5c2gifQ.44erZi0QyJwNw__0LsjcQQ'
                    />

                {this.state.markers.map((data, id) =>  <Marker key={'marker-'+id} position={[data.point.x,data.point.y]} icon={iconImage}><Popup>
                        <span> {data.namecoordinate} </span></Popup></Marker>)}
                </Map>
               </center>
            </div>
        );
    }
}

export default (LMap);
